                alert("Please enter a valid email address");
                $("[id*=txtFeedbackEmail]").focus();
                return false;
            }

            else if (Comments == "") {

                alert("Please enter some comments");
                $("[id*=txtFeedbackComments]").focus();
                return false;
            }

