            if (Name == "") {

                alert("Please enter your name");
                $("[id*=txtFeedbackName]").focus();
                return false;
            }
            else if (Email == "") {
                alert("Please enter your email");
                $("[id*=txtFeedbackEmail]").focus();
                return false;
            }
            else if (Email != "" && !ValidateEmail(Email)) {
                alert("Please enter a valid email address");
                $("[id*=txtFeedbackEmail]").focus();
