    function Search() {
        highlightTextAutocomplete();
        $(".autoSuggest").autocomplete({

            source: function (request, response) {
                var param = { keyWord: document.getElementById('txtSearch').value };

                $.ajax({
                    url: "../Home.aspx/GetKeywords",
